const JIKAN_API_BASE = 'https://api.jikan.moe/v4';
// Fungsi penunda untuk menghindari rate limiting API
async function rateLimit() {
    return new Promise((resolve)=>setTimeout(resolve, 300));
}
async function getAnimeDetail(mal_id) {
    try {
        await rateLimit();
        // Menggunakan cache: 'no-store' untuk memastikan selalu mengambil data terbaru dari Jikan
        const res = await fetch(`${JIKAN_API_BASE}/anime/${mal_id}/full`, {
            cache: 'no-store'
        });
        // Jika API merespons 404 atau status non-OK lainnya
        if (!res.ok) {
            // Log status untuk debugging di konsol
            console.error(`API returned status ${res.status} for ID ${mal_id}.`);
            if (res.status === 404) return null; // Anime tidak ditemukan
            throw new Error('Failed to fetch anime detail with status: ' + res.status);
        }
        const data = await res.json();
        return data.data;
    } catch (error) {
        console.error('Error fetching anime detail:', error);
        return null;
    }
}
